const crypto = require('crypto');

const generateId = () => {
  return crypto.randomUUID();
};

const formatTimestamp = (timestamp) => {
  const date = new Date(timestamp);
  return date.toLocaleString();
};

const generateRoomId = (prefix = 'room') => {
  return `${prefix}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
};

const isValidEmail = (email) => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
};

const truncateString = (str, maxLength) => {
  if (str.length <= maxLength) return str;
  return str.substring(0, maxLength) + '...';
};

const formatFileSize = (bytes) => {
  if (bytes === 0) return '0 Bytes';
  
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

const getFileExtension = (filename) => {
  return filename.split('.').pop().toLowerCase();
};

const isImageFile = (filename) => {
  const imageExtensions = ['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'];
  return imageExtensions.includes(getFileExtension(filename));
};

const generateAvatar = (username) => {
  const colors = [
    '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7',
    '#DDA0DD', '#98D8C8', '#F7DC6F', '#BB8FCE', '#85C1E9'
  ];
  
  const color = colors[username.length % colors.length];
  const initials = username.substring(0, 2).toUpperCase();
  
  return {
    color,
    initials,
    url: `https://ui-avatars.com/api/?name=${encodeURIComponent(username)}&background=${color.substring(1)}&color=fff`
  };
};

const rateLimitCheck = (userLimits, userId, action, limit = 10, windowMs = 60000) => {
  const now = Date.now();
  const userKey = `${userId}_${action}`;
  
  if (!userLimits[userKey]) {
    userLimits[userKey] = [];
  }
  
  userLimits[userKey] = userLimits[userKey].filter(timestamp => now - timestamp < windowMs);
  
  if (userLimits[userKey].length >= limit) {
    return false;
  }
  
  userLimits[userKey].push(now);
  return true;
};

const escapeHtml = (text) => {
  const map = {
    '&': '&amp;',
    '<': '&lt;',
    '>': '&gt;',
    '"': '&quot;',
    "'": '&#039;'
  };
  
  return text.replace(/[&<>"']/g, (m) => map[m]);
};

const parseLinks = (text) => {
  const urlRegex = /(https?:\/\/[^\s]+)/g;
  return text.replace(urlRegex, '<a href="$1" target="_blank" rel="noopener noreferrer">$1</a>');
};

const generateNotificationSound = (type) => {
  const sounds = {
    message: 'data:audio/wav;base64,',
    private: 'data:audio/wav;base64,',
    joined: 'data:audio/wav;base64,'
  };
  
  return sounds[type] || sounds.message;
};

const debounce = (func, wait) => {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

const throttle = (func, limit) => {
  let inThrottle;
  return function(...args) {
    if (!inThrottle) {
      func.apply(this, args);
      inThrottle = true;
      setTimeout(() => inThrottle = false, limit);
    }
  };
};

module.exports = {
  generateId,
  formatTimestamp,
  generateRoomId,
  isValidEmail,
  truncateString,
  formatFileSize,
  getFileExtension,
  isImageFile,
  generateAvatar,
  rateLimitCheck,
  escapeHtml,
  parseLinks,
  generateNotificationSound,
  debounce,
  throttle
};